import React, { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'

function NewAlbum() {
    const url = 'http://localhost:8000/api/albums/'
    const {id}= useParams()
    console.log(id)
    const [album, setAlbum] = useState({
        name: '',
        artist: Number(id)

    })
    const [image, newImage] = useState(null)
    const formData = new FormData();

    for (const name in data) {
        formData.append(name, data[name]);
    }

    const navigate = useNavigate()
    let token = localStorage.getItem('access_token')


    const handleChange = (event) => {
        if ([event.target.name] == 'image') {
            newImage({...image, image_url: event.target.files})
            console.log(event.target.files)
        } else {
        setAlbum({ ...album, [event.target.id]: event.target.value })
        console.log(album)}
    }



    const [networkErrMsg, setNetworkErrMsg] = useState(null)

    const statusCodeToErr = (responseObj) => {
        setNetworkErrMsg(`Network Error of code: ${responseObj.status}`)
        // TODO - console log the err message
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(`fetching with token ${token}`)
        setNetworkErrMsg(null)
        for (const name in album) {
            formData.append(name, album[name]);
        }
        formData.append(image.image_url, image.value)
        console.log(formData)
        fetch(url, 
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': ` JWT ${token}`,

                },
                body: album
            }
        )
            .then(res => {
                if (res.ok) {
                    // navigate(`/artist/${id}`)
                    return res.json()

                } else {
                    statusCodeToErr(res)
                    return Promise.resolve(null)

                }
            })
            .then(data => {
                if (!data) {
                    console.log(`problem with network request: ${networkErrMsg}`)
                } else {

                    console.log(data)

                    // call to refresh the list
                    // set RefreshCounter(refreshCounter + 1)
                }
            })
    }

    return (
        <>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">name</label>
                <input type="text" id="name" onChange={handleChange} value={album.name} /><br></br>
                <input accept='image/*' id="image_url" onChange={handleChange} name='image' type='file'/>
                <input type="submit" className="btn btn-danger" value="goodbye" />
            </form>
        </>
    )
}

export default NewAlbum
