import react from 'react'
import { useEffect, useState } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom'
import axios from 'axios'

function EditProfile() {
    const navigate = useNavigate()
    const userID = localStorage.getItem("id")
    const url = `http://localhost:8000/api/user/${userID}`
    const initialFormData = Object.freeze({
        email: '',
        username: '',
        password: '',
    });
    const [user, setUser] = useState(initialFormData)
    const [userAvatar, setUserAvatar] = useState(null)


    let token = localStorage.getItem('access_token')

    const handleChange = (event) => {
        if ([event.target.name] == 'avatar') {
            setUserAvatar({
                avatar: event.target.files
            })
        } else {
        setUser({ ...user, [event.target.id]: event.target.value })
        console.log(user) }
    }

    function componentDidMount() {
        axios.get(url)
            .then(res => {
                const data = res.data
                setUser(data)
            })
    }


    useEffect(() =>
        componentDidMount(), [])



    const [networkErrMsg, setNetworkErrMsg] = useState(null)

    const [clientErrMsg, setClientErrMsg] = useState(null)

    const statusCodeToErr = (responseObj) => {
        setNetworkErrMsg(`Network Error of code: ${responseObj.status}`)
        // TODO - console log the err message
    }
    const clientFormValidation = (user) => {
        const blankFields = Object.entries(user)
            .filter(kv => kv[1] === '')
        if (blankFields.length > 0) {
            setClientErrMsg(`${blankFields[0][0]} can not be blank`)
            return false
        }
        setClientErrMsg(null)
        return true
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(`fetching with token ${token}`)
        setNetworkErrMsg(null)
        if (!clientFormValidation(user)) {
            return
        }
        fetch(url,
            {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': ` JWT ${token}`
                },
                body: JSON.stringify(user)
            }
        )
            .then(res => {
                if (res.ok) {
                    navigate(`/`)
                    return res.json()
                } else {
                    statusCodeToErr(res)
                    return Promise.resolve(null)
                }
            })
            .then(data => {
                if (!data) {
                    console.log(`problem with network request: ${networkErrMsg}`)
                } else {

                    console.log(data)

                    // call to refresh the list
                    // set RefreshCounter(refreshCounter + 1)
                }
            })
    }







    return (
        <div>
            {!user
                ? "loading"
                : <>Edit your profile
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="email">Email</label>
                        <input type="text" id="email" onChange={handleChange} value={user.email} placeholder={user.email} /><br></br>
                        <label htmlFor="email">Username</label>
                        <input type="text" id="user_name" onChange={handleChange} value={user.user_name} placeholder={user.user_name} /><br></br>
                        <input accept='image/*' id="avatar" onChange={handleChange} name='avatar' type='file' />
                        <input type="submit" className="btn btn-danger" value="Update" />
                    </form>
                </>}

        </div>
    )
}


export default EditProfile
