import React from 'react'
import { useParams, Link } from 'react-router-dom'
import { useEffect, useState } from 'react'
import { Card, CardContent, CardMedia, Grid, Typography, Container } from '@mui/material'
import axios from 'axios'


function Album() {
    const [album, setAlbum] = useState(null)
    const { id } = useParams()
    const url = `http://localhost:8000/api/albums/${id}`
    console.log(url)
    function componentDidMount() {
        axios.get(url)
            .then(res => {
                const data = res.data
                setAlbum(data)
            })
    }

    useEffect(() =>
        componentDidMount(), [])

    function deletePost() {

    }

    return (
        <div>
            {!album
                ? "no data yet"
                : <div>
                    <h1>{album.name}</h1>
                    <Link to={`/artist/${album.artist_id}`}>{album.artist}</Link>
                    {album.posts.map(post => {
                        return(
                            <div>
                            <h1>{post.title} by {post.author_name}</h1>
                            <p>{post.content}</p>
                                {`${post.author_name}` === localStorage.getItem('user').replace(/['"]+/g, '')
                                ? <div><Link to={`/post/${post.id}/edit`}>Edit</Link>
                                    <button onSubmit={deletePost}>Delete</button></div>
                                :""
                                }
                            </div>
                        )
                    })}
                    <Link to={`/album/${album.id}/${album.artist_id}/newreview`}>Write a review</Link>


                </div>

            }
        </div>
    )
}

export default Album
