import { useParams } from 'react-router-dom'
function NewPost() {
    const url = 'http://localhost:8000/api/posts/'
    const {albumid, artistid} = useParams()
    const [newBird, setNewBird] = useState(newBirdForm)
    const [finalBird, setFinalBird] = useState()

    const newPostForm = {
        name: '',
        genus: '',
        conservationStatus: '',
        image: '',
        homepage: ''

    }

    const handleChange = (event) => {
        event.preventDefault()
        setNewBird({ ...newPostForm, [event.target.id]: event.target.value })
        console.log(newBird)
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        const birdCopy = [...finalBird]
        birdCopy.push(newBirdForm)
        console.log(birdCopy)
        setFinalBird(birdCopy)
        console.log(finalBird)
    }


    return (
        <>
        write review for {albumid} by {artistid}
        </>
    )
}

export default NewPost
