import react from 'react'
import { useEffect, useState } from 'react'
import { useParams, Link } from 'react-router-dom'
import axios from 'axios'
function EditPost() {
    const {postid} = useParams()
    const url = `http://localhost:8000/api/posts/${postid}`
    const [post, setPost] = useState(null)
    const [newPost, setnewPost] = useState({
        title: '',
        content: '',
        status: ''
    })


    let token = localStorage.getItem('access_token')

    const handleChange = (event) => {
        setnewPost({ ...newPost, [event.target.id]: event.target.value })
        console.log(newPost)
    }

    function componentDidMount() {
        axios.get(url)
            .then(res => {
                const data = res.data
                setPost(data)
            })
    }


    useEffect(() =>
        componentDidMount(), [])



    const [networkErrMsg, setNetworkErrMsg] = useState(null)

    const [clientErrMsg, setClientErrMsg] = useState(null)

    const statusCodeToErr = (responseObj) => {
        setNetworkErrMsg(`Network Error of code: ${responseObj.status}`)
        // TODO - console log the err message
    }
    const clientFormValidation = (newPost) => {
        const blankFields = Object.entries(newPost)
            .filter(kv => kv[1] === '')
        if (blankFields.length > 0) {
            setClientErrMsg(`${blankFields[0][0]} can not be blank`)
            return false
        }
        setClientErrMsg(null)
        return true
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(`fetching with token ${token}`)
        setNetworkErrMsg(null)
        if (!clientFormValidation(newPost)) {
            return
        }
        fetch(url,
            {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': ` JWT ${token}`
                },
                body: JSON.stringify(newPost)
            }
        )
            .then(res => {
                if (res.ok) {
                    return res.json()
                } else {
                    statusCodeToErr(res)
                    return Promise.resolve(null)
                }
            })
            .then(data => {
                if (!data) {
                    console.log(`problem with network request: ${networkErrMsg}`)
                } else {

                    console.log(data)

                    // call to refresh the list
                    // set RefreshCounter(refreshCounter + 1)
                }
            })
    }







    return (
        <>
            {!post
            ?"loading"
            : "ok"}

        </>
    )
}


export default EditPost
