import axios from 'axios'
import React, { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'

function NewAlbum() {
    const navigate = useNavigate()
    const url = 'http://localhost:8000/api/albums/'
    const {id}= useParams()
    const [album, setAlbum] = useState({
        name: '',
        artist: Number(id)

    })
    const [image, newImage] = useState({image_url: null})
    let token = localStorage.getItem('access_token')


    const handleChange = (event) => {
        if ([event.target.name] == 'image') {
            newImage({image_url: event.target.files[0]})
            console.log(event.target.files[0])
        } else {
        setAlbum({ ...album, [event.target.id]: event.target.value })
        console.log(album)}
    }



    const [networkErrMsg, setNetworkErrMsg] = useState(null)

    const statusCodeToErr = (responseObj) => {
        setNetworkErrMsg(`Network Error of code: ${responseObj.status}`)

    }




    const handleSubmit = (e) => {
        e.preventDefault()
        let formData = new FormData();
        formData.append('name', album.name);
        formData.append('artist', album.artist)
        formData.append('image_url', image.image_url)
        console.log(`fetching with token ${token}`)
        for (var key of formData.entries()) {
            console.log(key[0] + ', ' + key[1]);
        }
        setNetworkErrMsg(null)
        axios.post(url, 
            {
                method: 'POST',
                headers: {
                    'Authorization': ` JWT ${token}`,
                    'Content-Type': 'application/json',
                    accept: 'application/json',
                },
                formData

            }
        )
            .then(res => {
                if (res.ok) {
                    // navigate(`/artist/${id}`)
                    return res.json()

                } else {
                    statusCodeToErr(res)
                    return Promise.resolve(null)

                }
            })
            .then(data => {
                if (!data) {
                    console.log(`problem with network request: ${networkErrMsg}`)

                } else {

                    console.log(data)

                    // call to refresh the list
                    // set RefreshCounter(refreshCounter + 1)
                }
            })
    }

    return (
        <>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">name</label>
                <input type="text" id="name" onChange={handleChange} value={album.name} /><br></br>
                <input accept='image/*' id="image_url" onChange={handleChange} name='image' type='file'/>
                <input type="submit" className="btn btn-danger" value="goodbye" />
            </form>
        </>
    )
}

export default NewAlbum
